function Message({ role, content }) {
  return (
    <>
      {role == "user" ? (
        <div className="flex justify-end">
          <div className="w-fit bg-[#EFF6FF] mx-10 px-5 py-5 rounded-lg">
            <p className="text-wrap">{content}</p>
          </div>
        </div>
      ) : (
        <div className="flex justify-start">
          <div className="w-fit bg-[#E9A5F1] mx-10 px-5 py-5 rounded-lg">
            <p className="text-wrap">
              {content == "<|pending|>" ? (
                <>
                  <span class="inline-flex size-1 animate-ping rounded-full bg-sky-400 opacity-75"></span>
                  <span class="inline-flex size-1 animate-ping rounded-full bg-sky-400 opacity-75"></span>
                  <span class="inline-flex size-2 animate-ping rounded-full bg-sky-400 opacity-75"></span>
                </>
              ) : (
                content
              )}
            </p>
          </div>
        </div>
      )}
    </>
  );
}

export default Message;
