function Prompt({ socket_obj }) {
  console.log(socket_obj);

  return (
    <div className="rounded-lg bg-[#F3F4F6] w-200 py-6 text-[#4D6BFE]">
      <form
        action={"ws://" + window.location.host + "/ws/chatbot"}
        method="get"
        className="flex justify-center items-center gap-3 mx-5"
      >
        <textarea
          name="user_prompt"
          id="user_prompt"
          placeholder="Ask anything"
          rows={2}
          cols={70}
          wrap="soft"
          autoFocus
          required
          className="inline-block text-xl resize-none p-5 overscroll-contain outline-0"
        ></textarea>

        <button
          type="submit"
          className="rounded-full size-20 bg-[#D6DEE8] text-white
          hover:bg-[#4D6BFE] hover:cursor-pointer hover:duration-300 hover:ease-out "
        >
          Send
        </button>
      </form>
    </div>
  );
}

export default Prompt;
