import Message from "./Message";

function Box({ chat_history }) {
  chat_history = [];
  return (
    <div className="border-x-1 border-solid pt-15 pb-60 w-3xl max-w-3xl flex flex-col gap-5">
      {chat_history.map((turn, idx) => (
        <>
          <Message key={idx * 2} content={turn[0]} />
          <Message key={idx * 2 + 1} content={turn[1]} />
        </>
      ))}
    </div>
  );
}

export default Box;
