import { useState, useEffect } from "react";

import Box from "./components/Box";
import New from "./components/New";
import Prompt from "./components/Prompt";

function App() {
  const [num, setNum] = useState(0);

  useEffect(() => {
    let socket = new WebSocket(
      "ws://" + window.location.host + "/ws/searchforeveryone" // ws://localhost:3000/ws/searchforeveryone
    );
    socket.onmessage = (event) => {
      console.log(event.data);
    };
  }, []);

  function add() {
    setNum(num + 1);
  }

  return (
    <div className="">
      <div className="fixed block w-full text-center text-lg text-black border-b-1 border-indigo-500 bg-white py-2">
        Mr. WHY?
        <div>{num}</div>
        <button onClick={add}>ADD</button>
      </div>

      <div className="flex justify-center">
        <Box />
      </div>

      <div className="fixed block w-full text-center bottom-0 flex flex-col items-center">
        <New />
        <Prompt />
      </div>
    </div>
  );
}

export default App;
